      // define association here
    }
  };
  Allcode.init({
    key: DataTypes.STRING,
    type: DataTypes.STRING,
    valuEn: DataTypes.STRING,
    valueVi: DataTypes.STRING,
  }, {
    sequelize,
    modelName: 'Allcode',
  });
  return Allcode;
};

